import React from 'react'
import './App.css'

class JangKengGame extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      basics: {
        player: null,
        computer: null,
        judgement: null
      },
      i18n: null
    }
    this.URI = 'http://localhost:8080/i18n.json'
    this.LANG = 'en'
  }

  componentDidMount () {
    window
      .fetch(this.URI)
      .then(res => res.json())
  }

  render () {
    const { title } = this.state.i18n
    return (
      <>
        <h1>{title}</h1>
      </>
    )
  }
}

export default JangKengGame
