import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = { id: 2 }
    this.URI = 'https://data.corona.go.jp/converted-json/covid19japan-all.json'
  }

  componentDidMount () {
    this.loadData(this.URI)
  }

  async loadData (uri) {
    const data = await window
      .fetch(URI)
      .then(res => res.json())
      .then(json => json[0].area)
      .then(prefs =>
        prefs.map(o => {
          o.name_jp
        })
      )
    this.setState({ image: data.sprites.front_default })
  }

  render (props) {
    console.log(this.state)
    return <img src={props.image} />
  }
}

export default App
