import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      value: 'halloween'
    }
  }

  render () {
    return <TestView value={this.state.value} />
  }

  handleUpdate (event) {
    const value = event.target.value
    this.setState({value: value})
  }
}

const TestView = props => {
  return (
    <>
      <h1>{props.value}!!</h1>
      <p>how are you?</p>
    </>
  )
}

export default App
