import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = { value: 'How are you?' }
  }

  render () {
    return (
      <>
        <TestView value={this.state.value} />
        //
        <TestInput />
      </>
    )
  }
}

const TestView = props => {
  return <h1>{props.value}</h1>
}
