import React from 'react'
import './App.css'

class JangkengGamePage extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      user: null,
      cpu: null,
      judge: null
    }
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick (hand) {
    console.log(hand)
    const computer = Math.floor(Math.random() * 3)
    const judgement = this.judge(computer, hand)
    this.setState({ user: hand, cpu: computer, judge: judgement })
  }

  judge () {
    if (this.state.human == null) {
      return null
    } else {
      return (this.state.computer - this.state.human + 3) % 3
    }
  }

  render () {
    console.log(this.state)
    return (
      <>
        <h1>じゃんけん</h1>
        <JangkengBox onClick={this.handleClick} />
        <ScoreBox state={this.state} />
      </>
    )
  }
}

const JangkengBox = props => {
  const { onClick } = props
  return (
    <>
      <button onClick={() => onClick(0)}>グー</button>
      <button onClick={() => onClick(1)}>チョキ</button>
      <button onClick={() => onClick(2)}>パー</button>
    </>
  )
}
const ScoreBox = props => {
  const { user, cpu, judge } = props.status
  const { hands, judgements, header } = props.lang
  return (
    <table>
      <tbody>
        <tr>
          <th>{header[0]}</th>
          <td>{hands[user]}</td>
        </tr>
        <tr>
          <th>{header[1]}</th>
          <td>{hands[cpu]}</td>
        </tr>
        <tr>
          <th>{header[2]}</th>
          <td>{judgements[judge]}</td>
        </tr>
      </tbody>
    </table>
  )
}

export default JangkengGamePage
