import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = { id: 2 }
    this.URI = 'https://pokeapi.co/api/v2/pokemon/1'
  }

  componentDidMount () {
    this.loadData(this.URI)
  }

  async loadData (uri) {
    const URI = `${uri}/${id}`
    const data = await window.fetch(URI).then(res => res.json())
    this.setState({ image: data.sprites.front_default })
  }

  render () {
    console.log(this.state)
    return <img src={props.image} />
  }
}

export default App
