import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = { value: null }
    this.URI = 'https://data.corona.go.jp/converted-json/covid19japan-all.json'
  }

  componentDidMount () {
    this.loadData(this.URI)
  }

  async loadData (URI) {
    const data = await window
      .fetch(URI)
      .then(res => res.json())
      .then(json => json[0].area)
      .then(prefs =>
        prefs.map(o => ({
          name: o.name_jp,
          patients: o.npatients
        }))
      )
    this.setState(data)
  }

  render (props) {
    console.log(this.state)
    return <img src={props.image} />
  }
}

export default App
