import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = { pref: null }
    this.URI = 'https://pokeapi.co/api/v2/pokemon/1'
  }

  componentDidMount () {
    window
      .fetch(this.URI)
      .then(res => res.json())
      .then(json => json[0])
      .then(console.log)
  }

  render () {
    console.log(this.state)
    return (
      <h1>
        {this.state.pref}, {this.state.patients}
      </h1>
    )
  }
}

export default App
