import React, { Component } from 'react'

class Main extends Component {
  constructor (props) {
    super(props)
    this.state = {
      items: ['Apple', 'Orange', 'Pear']
    }
  }

  render () {
    return (
      <>
        <ListView items={this.state.items} />
      </>
    )
  }
}
const ListView = props => (
  <ul>
    {props.items.map((item, i) => (
      <li key={i}>{item}</li>
    ))}
  </ul>
)

export default Main
