import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = { pref: null }
    this.URI = 'https://pokeapi.co/api/v2/pokemon/1'
  }

  componentDidMount () {
    this.loadData(this.URI)
  }

  async loadData(uri) {
    const data = await window.fetch(uri)
      .then(res => res.json())
    this.setState({name:data.name})
  }

  render () {
    console.log(this.state)
    return (
      <h1>
        {this.state.pref}, {this.state.patients}
      </h1>
    )
  }
}

export default App
