import React from 'react'
import './App.css'

class App extends React.Component {
  constructor (props) {
    super(props)
    this.URI = 'https://data.corona.go.jp/converted-json/covid19japan-all.json'
  }

  componentDidMount () {
    window
      .fetch(this.URI)
      .then(res => res.json())
      .then(json => json[0].area[46])
      .then(data =>
        this.setState({
          pref: data.name_jp,
          patients: data.npatients
        })
      )
      .then(console.log)
  }

  render () {
    console.log(this.state)
    return (
      <h1>
        {this.state.pref}, {this.state.patients}
      </h1>
    )
  }
}

export default App
